use strict;
use warnings;

# We replace the keypad with a coordinate system of our own
# 1 2 3 --> (0,2) (1,2) (2,2)
# 4 5 6 --> (0,1) (1,1) (2,1)
# 7 8 9 --> (0,0) (1,0) (2,0)
my $xco = 1;
my $yco = 1;
while(<DATA>) {
	chomp;
	print "Line: $_\n";
	my @instructions = split //;
	foreach(@instructions) {
		if($_ eq 'U') {
			$yco++ if $yco < 2;
		} elsif($_ eq 'D') {
			$yco-- if $yco > 0;
		} elsif($_ eq 'L') {
			$xco-- if $xco > 0;
		} elsif($_ eq 'R') {
			$xco++ if $xco < 2;
		} else { die "should not happen\n" }
	}
	print translation($xco, $yco) . $/;
}

sub translation {
	my ($x, $y) = @_;
	return 1 if $x == 0 && $y == 2;
	return 2 if $x == 1 && $y == 2;
	return 3 if $x == 2 && $y == 2;
	return 4 if $x == 0 && $y == 1;
	return 5 if $x == 1 && $y == 1;
	return 6 if $x == 2 && $y == 1;
	return 7 if $x == 0 && $y == 0;
	return 8 if $x == 1 && $y == 0;
	return 9 if $x == 2 && $y == 0;
}

