use v5.40;

my @grid = ();
my $width = 0;
while(<DATA>) {
	chomp;
	if(!@grid) {
		$width = length($_) + 2;
		say "width is $width (incl. border)";
		my @toprow = split //, '.' x $width;
		push @grid, \@toprow;
	}
	my @row = split //, '.' . $_ . '.';
	push @grid, \@row;
}
my @bottomrow = split //, '.' x $width;
push @grid, \@bottomrow;

my $total = 0;
for(my $r = 1; $r < $width-1; $r++) {
	for(my $c = 1; $c < scalar(@grid)-1; $c++) {
		next if $grid[$r][$c] ne '@';
		my $n = 0;
		$n++ if $grid[$r-1][$c-1] eq '@';
		$n++ if $grid[$r-1][$c] eq '@';
		$n++ if $grid[$r-1][$c+1] eq '@';
		$n++ if $grid[$r][$c-1] eq '@';
		#$n++ if $grid[$r][$c] eq '@';
		$n++ if $grid[$r][$c+1] eq '@';
		$n++ if $grid[$r+1][$c-1] eq '@';
		$n++ if $grid[$r+1][$c] eq '@';
		$n++ if $grid[$r+1][$c+1] eq '@';
		if($n < 4) {
			$total++;
		}
	}
}

say "Solution: $total";

