use v5.40;

my @grid = ();
my $width = 0;
while(<DATA>) {
	chomp;
	if(!@grid) {
		$width = length($_) + 2;
		say "width is $width (incl. border)";
		my @toprow = split //, '.' x $width;
		push @grid, \@toprow;
	}
	my @row = split //, '.' . $_ . '.';
	push @grid, \@row;
}
my @bottomrow = split //, '.' x $width;
push @grid, \@bottomrow;

my $total = 0;
my @pos_to_remove = ();
while((@pos_to_remove = positions_to_remove())) {
	$total += scalar(@pos_to_remove);
	say "Removing " . scalar(@pos_to_remove);
	remove(@pos_to_remove);
}
say "Solution: $total";

sub positions_to_remove {
	my @pos = ();
	for(my $r = 1; $r < $width-1; $r++) {
		for(my $c = 1; $c < scalar(@grid)-1; $c++) {
			next if $grid[$r][$c] ne '@';
			my $n = 0;
			$n++ if $grid[$r-1][$c-1] eq '@';
			$n++ if $grid[$r-1][$c] eq '@';
			$n++ if $grid[$r-1][$c+1] eq '@';
			$n++ if $grid[$r][$c-1] eq '@';
			#$n++ if $grid[$r][$c] eq '@';
			$n++ if $grid[$r][$c+1] eq '@';
			$n++ if $grid[$r+1][$c-1] eq '@';
			$n++ if $grid[$r+1][$c] eq '@';
			$n++ if $grid[$r+1][$c+1] eq '@';
			if($n < 4) {
				push @pos, "$r $c";
			}
		}
	}
	return @pos;
}

sub remove {
	foreach(@_) {
		my ($r, $c) = split / /;
		$grid[$r][$c] = '.';
	}
}


