use v5.38;
my $i = 0;
$_ = <DATA>;
chomp;
my @arr = split / /;
say "Initieel: @arr";
my $steps = 0;
while($arr[$i]) {
	my $c = $arr[$i];
	$arr[$i] = 0;
	$steps++; # taking them all in your hands is 1 step
	$i++;
	while($c) {
		$arr[$i%@arr] += 1;
		$steps++;
		$i++;
		$c--;
	}
	$i = $i % @arr;
}

say "steps: $steps";

